  for(set(hotspotSoundi, 0), hotspotSoundi LT hotspot.count, inc(hotspotSoundi),
    if(hotspot[get(hotspotSoundi)].pauseothersounds,
      if(hotspot[get(hotspotSoundi)].stateVideo == 'playing',
        set(killDirectionalSound, true);
      );
    );
  );
