    ,
      if(device.html5,
        set(spotsound207_started, true);
        set(spotsound207_paused, false);
        playsound(spotsound207, "_1080_1083108010861090107710821072.mp3", 1);
      );
    );
  );
  );
  if(device.flash,
    set(hotspot[spotvideo208].volume, get(volume));
  ,
    ifnot(device.ios,
      js(kpanotour.Sounds.startVideosSounds(get(volume)));
    );
  );
,
  if(device.flash,
    set(hotspot[spotvideo208].volume, 0);
  ,
    ifnot(device.ios,
      js(kpanotour.Sounds.stopVideosSounds(get(volume)));
    );
  );
);

